mapper.insert:
insert into vm_admin_filter_log (id, operator, url,
  param, ip, createtime
  )
values (#{id,jdbcType=VARCHAR}, #{operator,jdbcType=VARCHAR}, #{url,jdbcType=VARCHAR}, 
  #{param,jdbcType=VARCHAR}, #{ip,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.select:
select id, operator, url, param, createtime from vm_admin_filter_log
1=1
and operator = #{operator,jdbcType=VARCHAR}
and url=#{url,jdbcType=VARCHAR}
and param LIKE CONCAT('%',#{param},'%')
ORDER BY createtime DESC
