mapper.select:
select userinfo.user_id as userId, userinfo.user_name as userName from base_user_info userinfo left join base_user_role user_role on userinfo.user_name = user_role.user_id left join base_user_region userregion on userinfo.user_name=userregion.user_id left join meta_data_item region on userregion.region=region.region and region.type='SupportedRegionList' where 1=1
and userinfo.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')
and userinfo.real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')
and user_role.role_code = #{roleCode,jdbcType=VARCHAR}
and userregion.region = #{region,jdbcType=VARCHAR}
group by userinfo.user_id ORDER BY userinfo.created_time DESC LIMIT #{pageIndex}, #{pageSize}

select count(1) from( select userinfo.user_id from base_user_info userinfo left join base_user_role user_role on userinfo.user_name = user_role.user_id left join base_user_region userregion on userinfo.user_name=userregion.user_id left join meta_data_item region on userregion.region=region.region and region.type='SupportedRegionList' where 1=1
and userinfo.user_name like concat('%',#{userName,jdbcType=VARCHAR},'%')
and userinfo.real_name like concat('%',#{realName,jdbcType=VARCHAR},'%')
and user_role.role_code = #{roleCode,jdbcType=VARCHAR}
and userregion.region = #{region,jdbcType=VARCHAR}
group by userinfo.user_id ) userTemp

