mapper.select:

select 

from conf_cluster_app
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}

mapper.delete:

delete from conf_cluster_app
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}

mapper.update:

update conf_cluster_app


    app_version = #{appVersion,jdbcType=VARCHAR},


    createdby = #{createdby,jdbcType=VARCHAR},


    created_time = #{createdTime,jdbcType=TIMESTAMP},


where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}


update conf_cluster_app
set app_version = #{appVersion,jdbcType=VARCHAR},
  createdby = #{createdby,jdbcType=VARCHAR},
  created_time = #{createdTime,jdbcType=TIMESTAMP}
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}

mapper.select:
select

from conf_cluster_app where 1=1

  and cluster_id = #{clusterId,jdbcType=VARCHAR}


  and app_name = #{appName,jdbcType=VARCHAR}


  and app_version = #{appVersion,jdbcType=VARCHAR}


  and createdby = #{createdby,jdbcType=VARCHAR}


  and #{createdTime,jdbcType=TIMESTAMP}
