mapper.select:

select 

from conf_cluster_tag
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and tag_group = #{tagGroup,jdbcType=VARCHAR}

mapper.delete:

delete from conf_cluster_tag
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and tag_group = #{tagGroup,jdbcType=VARCHAR}

mapper.insert:

insert into conf_cluster_tag (cluster_id, tag_group, tag_val
  )
values (#{clusterId,jdbcType=VARCHAR}, #{tagGroup,jdbcType=VARCHAR}, #{tagVal,jdbcType=VARCHAR}
  )

mapper.update:

update conf_cluster_tag


    tag_val = #{tagVal,jdbcType=VARCHAR},


where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and tag_group = #{tagGroup,jdbcType=VARCHAR}


update conf_cluster_tag
set tag_val = #{tagVal,jdbcType=VARCHAR}
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and tag_group = #{tagGroup,jdbcType=VARCHAR}

mapper.select:
select

from conf_cluster_tag where 1=1

  and cluster_id = #{clusterId,jdbcType=VARCHAR}


  and tag_group = #{tagGroup,jdbcType=VARCHAR}


  and tag_val = #{tagVal,jdbcType=VARCHAR}

select
distinct tag_val
from conf_cluster_tag where 1=1

  and cluster_id = #{clusterId,jdbcType=VARCHAR}


  and tag_group = #{tagGroup,jdbcType=VARCHAR}


  and tag_val = #{tagVal,jdbcType=VARCHAR}
