mapper.select:
select
ic.cluster_id, ic.ambari_username, ic.ambari_password, ic.ambari_host, ic.master_ips, ic.ambari_count,
ic.master_vms_count, ic.core_vms_count,
ic.task_vms_count, ic.apps_count, ic.cluster_create_begtime, ic.cluster_create_endtime,
cc.state, cc.cluster_name, cc.is_ha, cc.created_time, cc.delete_protected,
cc.creation_sub_state,cc.region
from info_cluster ic left join conf_cluster cc on cc.cluster_id=ic.cluster_id where 1=1

  and ic.cluster_id = #{clusterId,jdbcType=VARCHAR}


  and cc.region = #{region,jdbcType=VARCHAR}


  and cc.cluster_name like CONCAT('%',#{clusterName,jdbcType=VARCHAR},'%')


  and cc.state in

    #{id}



  and ic.cluster_id in

    #{item}


order by cc.created_time desc

  limit #{pageIndex}, #{pageSize}

select count(1) from info_cluster ic left join conf_cluster cc on cc.cluster_id=ic.cluster_id where 1=1

  and ic.cluster_id = #{clusterId,jdbcType=VARCHAR}


  and cc.region = #{region,jdbcType=VARCHAR}


  and cc.cluster_name like CONCAT('%',#{clusterName,jdbcType=VARCHAR},'%')


  and cc.state in

    #{id}



  and ic.cluster_id in

    #{item}

select sku_name as skuName,COUNT(sku_name) as count from (
select isgsti.cluster_id,isgsti.vm_name,icv.sku_name   from info_spot_group_scale_task_item isgsti
inner join info_cluster_vm icv  on isgsti.cluster_id=icv.cluster_id and isgsti.vm_name =icv.vm_name

    inner join conf_cluster cc on icv.cluster_id=cc.cluster_id
    and cc.region=#{dc,jdbcType=VARCHAR}


and isgsti.created_time > #{begTime,jdbcType=TIMESTAMP}


 and isgsti.created_time < #{endTime,jdbcType=TIMESTAMP}


  and icv.sku_name=#{skuName,jdbcType=VARCHAR}


   and isgsti.cluster_id =#{clusterId,jdbcType=VARCHAR}

group by isgsti.cluster_id,isgsti.vm_name,icv.sku_name) as t1 group by sku_name;

select sku_name as skuName,count(sku_name) as count  from info_cluster_vm icv

  inner join conf_cluster cc on icv.cluster_id=cc.cluster_id
  and cc.region=#{dc,jdbcType=VARCHAR}

where purchase_type =2

  and icv.cluster_id =#{clusterId,jdbcType=VARCHAR}


  and icv.sku_name=#{skuName,jdbcType=VARCHAR}


  and create_begtime > #{begTime,jdbcType=TIMESTAMP}


  and create_begtime < #{endTime,jdbcType=TIMESTAMP}

group by sku_name
