mapper.select:
select 

from base_release_apps
where release_version = #{releaseVersion,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}

mapper.delete:
delete from base_release_apps
where release_version = #{releaseVersion,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}

mapper.insert:
insert into base_release_apps (release_version, app_name, app_verison, 
  required, created_by, created_time
  )
values (#{releaseVersion,jdbcType=VARCHAR}, #{appName,jdbcType=VARCHAR}, #{appVerison,jdbcType=VARCHAR}, 
  #{required,jdbcType=INTEGER}, #{createdBy,jdbcType=VARCHAR}, #{createdTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update base_release_apps
set app_verison = #{appVerison,jdbcType=VARCHAR},
  required = #{required,jdbcType=INTEGER},
  created_by = #{createdBy,jdbcType=VARCHAR},
  created_time = #{createdTime,jdbcType=TIMESTAMP}
where release_version = #{releaseVersion,jdbcType=VARCHAR}
  and app_name = #{appName,jdbcType=VARCHAR}
