mapper.insert:
insert into conf_cluster_script (conf_script_id, cluster_id, script_name, 
  run_timing, script_path, script_param, 
  sort_no, createdby, created_time
  )
values (#{confScriptId,jdbcType=VARCHAR}, #{clusterId,jdbcType=VARCHAR}, #{scriptName,jdbcType=VARCHAR}, 
  #{runTiming,jdbcType=VARCHAR}, #{scriptPath,jdbcType=VARCHAR}, #{scriptParam,jdbcType=VARCHAR}, 
  #{sortNo,jdbcType=INTEGER}, #{createdby,jdbcType=VARCHAR}, #{createdTime,jdbcType=TIMESTAMP}
  )

mapper.select:
select

from conf_cluster_script

  cluster_id = #{clusterId,jdbcType=VARCHAR} and run_timing = #{runTiming,jdbcType=VARCHAR}

order by sort_no;
