mapper.select:
select 

from info_cluster_vm_index_history
where cluster_id = #{clusterId,jdbcType=VARCHAR}
and vm_role = #{vmRole,jdbcType=VARCHAR}
and task_id = #{taskId,jdbcType=VARCHAR}

mapper.update:
update
  info_cluster_vm_index_history
set
  create_time = #{createTime,jdbcType=TIMESTAMP},
  modified_time = #{modifiedTime,jdbcType=TIMESTAMP},
  task_id = #{taskId,jdbcType=VARCHAR},
  before_index = #{beforeIndex,jdbcType=INTEGER},
  after_index = #{afterIndex,jdbcType=INTEGER},
  delta_index = #{deltaIndex,jdbcType=INTEGER}
where
  cluster_id = #{clusterId,jdbcType=VARCHAR}
  and vm_role = #{vmRole,jdbcType=VARCHAR}
  and task_id = #{taskId,jdbcType=VARCHAR}
