mapper.select:
select

from info_cluster_vm_job
where cluster_id = #{clusterId,jdbcType=VARCHAR}
   and operation_type= #{operationType,jdbcType=VARCHAR}

   and activity_log_id = #{activityLogId,jdbcType=VARCHAR}

select icvj.* from info_cluster_vm_job icvj
 inner join  info_cluster_operation_plan_activity_log icopal  on icvj.activity_log_id=icopal.activity_log_id
 and icvj.operation_type='scaleout' and icopal.plan_id =#{planId,jdbcType=VARCHAR}
