mapper.select:
select
from azure_cleaned_vms_record where 1=1
AND cluster_id = #{clusterId,jdbcType=VARCHAR}
AND cluster_name like CONCAT(CONCAT('%', #{clusterName}), '%')
AND vm_name like CONCAT(CONCAT('%', #{vmName}), '%')
ORDER BY created_time DESC limit #{pageIndex},#{pageSize}

mapper.update:
update azure_cleaned_vms_record
set  vms_del_response = #{vmsDelResponse,jdbcType=VARCHAR}
where cluster_name = #{clusterName,jdbcType=VARCHAR}
