mapper.update:
update base_release_version
set release_description = #{releaseDescription,jdbcType=VARCHAR},
  create_by = #{createBy,jdbcType=VARCHAR},
  create_time = #{createTime,jdbcType=TIMESTAMP}
where release_version = #{releaseVersion,jdbcType=VARCHAR}

mapper.select:
select bi.img_id, brvi.release_version, bi.os_image_id, bi.os_image_type, bi.os_version, bi.created_time
from base_release_vm_img brvi
         inner JOIN base_images bi on brvi.img_id = bi.img_id


      brvi.release_version = #{releaseVersion,jdbcType=VARCHAR}


order by bi.created_time desc
limit #{pageStart},#{pageLimit}
