mapper.delete:

delete from base_release_vm_img
where release_version = #{releaseVersion,jdbcType=VARCHAR}
  and vm_role = #{vmRole,jdbcType=VARCHAR}

mapper.update:

update base_release_vm_img
set os_imageid = #{osImageid,jdbcType=VARCHAR},
  os_image_type = #{osImageType,jdbcType=VARCHAR},
  os_version = #{osVersion,jdbcType=VARCHAR},
  createdby = #{createdby,jdbcType=VARCHAR},
  created_time = #{createdTime,jdbcType=TIMESTAMP},
  img_id = #{imgId,jdbcType=VARCHAR}
where release_version = #{releaseVersion,jdbcType=VARCHAR}
  and vm_role = #{vmRole,jdbcType=VARCHAR}

mapper.select:
select bi.img_id, brvi.release_version, bi.os_image_id, bi.os_image_type, bi.os_version, bi.created_time
from base_release_vm_img brvi
inner JOIN base_images bi on brvi.img_id = bi.img_id


    brvi.release_version = #{releaseVersion,jdbcType=VARCHAR}


order by bi.created_time desc
limit #{pageStart},#{pageLimit}
