mapper.select:
select

from info_ambari_config_group
where group_id = #{groupId,jdbcType=VARCHAR}

    and state in

        #{state}

select

from info_ambari_config_group
where cluster_id = #{clusterId,jdbcType=VARCHAR}
  and ambari_group_name = #{ambariGroupName,jdbcType=VARCHAR}
  and vm_sku_id = #{vmSkuId,jdbcType=VARCHAR}

select

from info_ambari_config_group
where cluster_id = #{clusterId,jdbcType=VARCHAR}
and ambari_group_name = #{ambariGroupName,jdbcType=VARCHAR}
and vm_sku_id = #{vmSkuId,jdbcType=VARCHAR}

select

from info_ambari_config_group
where cluster_id = #{clusterId,jdbcType=VARCHAR} and ambari_id = #{ambariId,jdbcType=BIGINT}

mapper.update:
update info_ambari_config_group
set state = #{state,jdbcType=INTEGER}
where group_id = #{groupId,jdbcType=VARCHAR}
