mapper.select:
select

from info_cluster_vm_delete
where cluster_id = #{clusterId,jdbcType=VARCHAR} and vm_name = #{vmName,jdbcType=VARCHAR}

select

from info_cluster_vm_delete
where status = 0 order by priority desc,created_time asc limit #{limitCount,jdbcType=INTEGER}

select

from info_cluster_vm_delete
where status =1  and beg_send_request_time < #{date,jdbcType=TIMESTAMP}

select

from info_cluster_vm_delete
where status =2  and get_delete_jobId_time < #{date,jdbcType=TIMESTAMP}

select

from info_cluster_vm_delete
where status = -1 and release_freeze_time < #{date,jdbcType=TIMESTAMP}

select jf.*,
cc.cluster_name,
op.plan_name,
vm.host_name,cc.region
from info_cluster_vm_delete as jf
left join info_cluster_vm as vm on vm.vm_name = jf.vm_name and vm.cluster_id =jf.cluster_id
left join conf_cluster as cc on cc.cluster_id = jf.cluster_id
left join info_cluster_operation_plan as op on op.plan_id = jf.plan_id
where jf.created_time  >=  #{beginDate}
and jf.created_time  <=  #{endDate}

  and op.plan_name = #{planName}


  and jf.plan_id = #{planId}


  and jf.cluster_id = #{clusterId}


  and cc.cluster_name like CONCAT(CONCAT('%', #{clusterName}), '%')


  and jf.vm_name like CONCAT(CONCAT('%', #{hostName}), '%')


  and jf.status = #{status}


  and cc.region = #{region}

order by jf.created_time desc
limit #{pageStart},#{pageLimit}

select
count(jf.id)
from info_cluster_vm_delete as jf
left join info_cluster_vm as vm on vm.vm_name = jf.vm_name and vm.cluster_id =jf.cluster_id
left join conf_cluster as cc on cc.cluster_id = jf.cluster_id
left join info_cluster_operation_plan as op on op.plan_id = jf.plan_id
where jf.created_time  >=  #{beginDate}
and jf.created_time  <=  #{endDate}

  and op.plan_name = #{planName}


  and jf.plan_id = #{planId}


  and jf.cluster_id = #{clusterId}


  and cc.cluster_name like CONCAT(CONCAT('%', #{clusterName}), '%')


  and jf.vm_name like CONCAT(CONCAT('%', #{hostName}), '%')


  and jf.status = #{status}


  and cc.region = #{region}

select case status when -1 then 'frozen' when 0 then 'waitDelete' when 1 then 'deleteRequest' when 2 then 'deleting' when 3 then 'deleted' end status, count(*) as vm_count from info_cluster_vm_delete
region = #{region}
group by status;
