mapper.select:
select

from info_cluster_vm_req_job_failed
where cluster_id=#{clusterId,jdbcType=VARCHAR} and plan_Id = #{planId,jdbcType=VARCHAR}
    and job_id = #{jobId,jdbcType=VARCHAR}

select
jf.*,
cc.cluster_name,
op.plan_name,cc.region
from info_cluster_vm_req_job_failed as jf
left join conf_cluster as cc on cc.cluster_id = jf.cluster_id
left join info_cluster_operation_plan as op on op.plan_id = jf.plan_id
where jf.created_time  >=  #{beginDate}
and jf.created_time  <=  #{endDate}

  and op.plan_name = #{planName}


  and jf.plan_id = #{planId}


  and jf.cluster_id = #{clusterId}


  and cc.cluster_name = #{clusterName}


  and jf.job_id = #{jobId}


  and jf.status = #{status}


  and cc.region = #{region}

order by jf.created_time desc
limit #{pageStart},#{pageLimit}

select
count(jf.id)
from info_cluster_vm_req_job_failed as jf
left join conf_cluster as cc on cc.cluster_id = jf.cluster_id
left join info_cluster_operation_plan as op on op.plan_id = jf.plan_id
where jf.created_time  >=  #{beginDate}
and jf.created_time  <=  #{endDate}

  and op.plan_name = #{planName}


  and jf.plan_id = #{planId}


  and jf.cluster_id = #{clusterId}


  and cc.cluster_name = #{clusterName}


  and jf.job_id = #{jobId}


  and jf.status = #{status}


  and cc.region = #{region}

select case status when 0 then 'init' when 1 then 'done' else 'init' end as status, count(*) as vm_req_count from info_cluster_vm_req_job_failed icvrjf inner join conf_cluster cc on icvrjf.cluster_id = cc.cluster_id
cc.region = #{region}
group by status ;
